find .work/ovh/ovh/website/docs/{d,r} -name '*.markdown' | while read f; do

name=$(basename $f | sed -e 's#.markdown##g' -e 's#.html.markdown##g')
cat > temp <<EOT
---
# generated by https://github.com/hashicorp/terraform-plugin-docs
page_title: "$name Resource - terraform-provider-ovh"
subcategory: ""
description: |-
---
EOT

echo "Fixed $f"

mv temp $f
done
